    @Override
    public void onUpgrade(SQLiteDatabase db, int oldVersion, int newVersion) {

        if (newVersion != DATABASE_VERSION) {
            this.deleteDataBase(db);
            this.createDataBase(db);
        }
    }


    private void deleteDataBase(SQLiteDatabase db) {
        String sql;

        sql = "DROP TABLE IF EXISTS gender";
        db.execSQL(sql);

        sql = "DROP TABLE IF EXISTS teacher";
        db.execSQL(sql);

        sql = "DROP TABLE IF EXISTS course";
        db.execSQL(sql);

        sql = "DROP TABLE IF EXISTS student";
        db.execSQL(sql);

        sql = "DROP TABLE IF EXISTS subject";
        db.execSQL(sql);

        sql = "DROP TABLE IF EXISTS homework";
        db.execSQL(sql);
    }

    private void createDataBase(SQLiteDatabase db) {
        String sql;

        sql = "CREATE TABLE IF NOT EXISTS gender (" +
                "id INTEGER PRIMARY KEY," +
                "name TEXT," +
                "description TEXT)";
        db.execSQL(sql);

        sql = "CREATE TABLE IF NOT EXISTS teacher (" +
                "id INTEGER PRIMARY KEY," +
                "name TEXT," +
                "lastName TEXT," +
                "birthDate TEXT," +
                "idGender INTEGER," +
                "email TEXT," +
                "phone TEXT," +
                "communicationDay TEXT," +
                "communicationHour TEXT," +
                "isTutor INTEGER," +
                "address TEXT," +
                "img TEXT," +
                "FOREIGN KEY(idGender) REFERENCES gender(id)," +
                "FOREIGN KEY(address) REFERENCES address(id))";
        db.execSQL(sql);

        sql = "CREATE TABLE IF NOT EXISTS course (" +
                "id INTEGER PRIMARY KEY," +
                "typeEducation TEXT," +
                "courseNumber INTEGER)";
        db.execSQL(sql);

        sql = "CREATE TABLE IF NOT EXISTS student (" +
                "id INTEGER PRIMARY KEY," +
                "name TEXT," +
                "lastName TEXT," +
                "birthDate TEXT," +
                "idGender INTEGER," +
                "email TEXT," +
                "phone TEXT," +
                "idCourse INTEGER," +
                "nameFather TEXT," +
                "fatherPhone TEXT," +
                "nameMother TEXT," +
                "motherPhone TEXT," +
                "address TEXT," +
                "img TEXT," +
                "FOREIGN KEY(idCourse) REFERENCES course(id)," +
                "FOREIGN KEY(idGender) REFERENCES gender(id)," +
                "FOREIGN KEY(address) REFERENCES address(id))";
        db.execSQL(sql);

        sql = "CREATE TABLE IF NOT EXISTS subject (" +
                "id INTEGER PRIMARY KEY," +
                "name TEXT," +
                "department TEXT," +
                "description TEXT," +
                "idTeacher INTEGER," +
                "FOREIGN KEY(idTeacher) REFERENCES teacher(id))";
        db.execSQL(sql);

        sql = "CREATE TABLE IF NOT EXISTS homework (" +
                "id INTEGER PRIMARY KEY," +
                "matter TEXT," +
                "description TEXT," +
                "deliveryDate TEXT," +
                "idSubject INTEGER," +
                "FOREIGN KEY(idSubject) REFERENCES subject(id))";
        db.execSQL(sql);
    }
